<?phprequire_once 'Usuario.php';class Archivo {    //--METODOS DE CLASE    public static function Subir($id) {        //variable de retorno        $retorno["Exito"] = TRUE;        $destino = "";        if ($id == Null) {            $objetoAccesoDatos = AccesoDatos::dameUnObjetoAcceso();            $id = $objetoAccesoDatos->RetornarUltimoIdInsertado();            $archivoTMmp = $id.".jpg";            $destino = "fotos/".$archivoTMmp;        }else{            $archivoTMmp = $id.".jpg";            $destiono = "fotos/".$archivoTMmp;        }           $tipoArchivo = pathinfo($_FILES["archivo"]["name"], PATHINFO_EXTENSION);        if ($_FILES["archivo"]["size"] > 80000){//800000) {            $retorno["Exito"] = FALSE;            $retorno["Mensaje"] = "El archivo es demasiado grande. Verifique!!!";            return $retorno;        } else {// ES UNA IMAGEN           //SOLO PERMITO CIERTAS EXTENSIONES           if($tipoArchivo != "jpg" && $tipoArchivo != "jpeg" && $tipoArchivo != "gif"            && $tipoArchivo != "png") {            $retorno["Exito"] = FALSE;            $retorno["Mensaje"] = "S&oacute;lo son permitidas imagenes con extensi&oacute;n JPG, JPEG, PNG o GIF.";            return $retorno;          }        }        if (!move_uploaded_file($_FILES["archivo"]["tmp_name"], $destino)) {            $retorno["Exito"] = FALSE;            $retorno = "Ocurrio un error al subir el archivo. No pudo guardarse.";            return $retorno;        }else{            $retorno = "Archivo subido exitosamente!!!";            $_GET['imatmp'] = $archivoTMmp ;            return $retorno;        }    }    public static function Borrar($path) {		unlink($path);    }    public static function Mover($pathOrigen, $pathDestino) {		return copy($pathOrigen, $pathDestino);    }}